string1 = ""
set1 = set(string1)
longest = 0
odd = False
for item in set1:
    times = string1.count(item)
    if times%2 == 0:
        longest += times
    else:
        longest += (times - 1)
        odd = True
if odd is True:
    longest += 1
print(longest)